INSERT INTO `products` (`id`, `name`, `description`, `content`, `menu_id`, `price`, `price_sale`, `active`, `created_at`, `updated_at`, `thumb`) VALUES
(6, 'Nước hoa nam Dior Sauvage Parfum', 'Mùi hương nước hoa của phiên bản lần này nhẹ mà lôi cuốn hơn hai phiên bản trước. Về độ lưu hương của nước hoa cũng lâu hơn từ 6-8 tiếng.', '<p><strong>Dior Sauvage Parfum</strong>&nbsp;ch&iacute;nh l&agrave; tiếp nối sự th&agrave;nh c&ocirc;ng của 2 đ&agrave;n anh đi trước của m&igrave;nh. Đ&oacute; ch&iacute;nh l&agrave; D<em>ior Sauvage Parfum EDP v&agrave; Dior Sauvage Parfum EDT</em>. Chai nước hoa n&agrave;y ra mắt v&agrave;o năm 2019 đ&atilde; được n&acirc;ng cấp l&ecirc;n. Mang n&eacute;t đằm thắm hơn nhưng vẫn giữ nguy&ecirc;n những th&agrave;nh phần then chốt tạo n&ecirc;n thương hiệu &ldquo;Lady Killer&rdquo;. Dior Sauvage Parfum l&agrave; mẫu nước hoa lấy cảm hứng từ tr&ecirc;n thảo nguy&ecirc;n v&agrave;o ban đ&ecirc;m. B&ecirc;n dưới l&agrave; &aacute;nh lửa trại len lỏi trong m&agrave;n đ&ecirc;m khiến mọi người với ấn tượng đầu ti&ecirc;n d&agrave;nh cho sản phẩm</p>', 18, 3950000, 3800000, 1, '2023-03-04 02:28:07', '2023-03-04 02:28:07', '/storage/uploads/2023/03/04/Dior-Sauvage-Parfum.jpg'),
(7, 'Giorgio Armani Acqua Di Gio Pour Homme', 'Về độ lưu hương của ‘Giò trắng’ sẽ lưu được trong khoảng 3-6 tiếng. Bên cạnh đó, sau khi dùng hơn 6 tiếng đồng hồ mình vẫn cảm nhận được mùi hương thơm nhẹ', '<p><strong>Giorgio Armani Acqua Di Gio Pour Homme</strong>&nbsp;l&agrave; loại nước hoa tới trong khoảng đất nước Ph&aacute;p. L&agrave; một đất nước với phổ th&ocirc;ng mẫu nước hoa nức tiếng tr&ecirc;n to&agrave;n cầu. Sản phẩm n&agrave;y được x&acirc;y dựng thương hiệu trong khoảng năm 1996. Đ&atilde; c&oacute; rất nhiều kh&aacute;ch h&agrave;ng ưa chuộng v&agrave; trở l&agrave; một tuyệt t&aacute;c kinh điển trong to&agrave;n cầu nước hoa cao cấp . C&aacute;c người t&igrave;nh mến nước hoa đ&atilde; đặt một chiếc t&ecirc;n &lsquo; Gi&ograve; trắng&rsquo; cực k&igrave; dễ thương cho d&ograve;ng nước hoa n&agrave;y. Với thiết kế thuần tu&yacute;, tinh tế v&agrave; lịch l&atilde;m.&nbsp;<em>Giorgio Armani Acqua Di Gio Pour Homme</em>&nbsp;tạo cho kh&aacute;ch h&agrave;ng cảm gi&aacute;c đẳng cấp mỗi l&uacute;c cầm n&oacute;.</p>', 18, 2500000, 1690000, 1, '2023-03-04 02:30:45', '2023-03-04 02:30:45', '/storage/uploads/2023/03/04/nuoc-hoa-nam-giorgio-armani-acqua-di-gio-pour-homme-edt-100ml-62982a64e47f0-02062022101132.jpg'),
(8, 'Burberry The Beat For Men', 'Mùi hương của sự đi lại, năng lượng và tuổi trẻ tạo nên phần đặc thù cho sản phẩm. Burberry The Beat For Men thích hợp cho những chàng trai sở hữu cuộc sống bận rộn hay vận động lúc đó hương thơm của nước hoa sẽ làm bạn thư giãn dù là bất cứ đâu.', '<p>Một loại&nbsp;<strong>nước hoa cho nam</strong>&nbsp;lấy cảm hứng trong khoảng &acirc;m nhạc, &acirc;m thanh, c&aacute; t&iacute;nh, nghệ thuật v&agrave; bề ngo&agrave;i. Đ&atilde; tạo n&ecirc;n 1&nbsp;<strong><em>Burberry The Beat For Men</em></strong>&nbsp;của ng&agrave;y bữa nay. Tới trong khoảng nh&atilde;n hiệu Burberry sau khi ra mắt mẫu nước hoa cho nữ. Th&igrave; cảm hứng đ&oacute; vẫn c&ograve;n n&ecirc;n đ&atilde; cho ra mắt năm v&agrave;o năm 2008 t&iacute;nh cho tới hiện tại vẫn lu&ocirc;n nằm trong Top nước hoa nam đ&aacute;ng d&ugrave;ng nhất. Burberry The Beat For Men sở hữu tr&ecirc;n m&igrave;nh một tẹo lịch l&atilde;m bởi c&aacute;c mẫu kẻ. L&agrave; biểu tượng của nh&atilde;n hiệu Burberry, phần dung dịch mang m&agrave;u xanh lục nhẹ.&nbsp;</p>', 18, 2500000, 1140000, 1, '2023-03-04 02:33:35', '2023-03-04 02:33:35', '/storage/uploads/2023/03/04/OIP.jpg'),
(9, 'Gucci Guilty Black', 'Nước hoa nam Gucci Guilty Black phù hợp cho những chàng trai với phong cách hào phóng, tạo bạo và một chút ngông cuồng. Độ lưu hương cũng như các dòng EDT khác từ 3-6 giờ. Về độ tỏa hương chỉ 1 cánh tay. Bên cạnh đó sản phẩm với hai dung tích cho bạn chọn lọc 50ml và 90ml.', '<p>1 loại nước hoa mang n&eacute;t ti&ecirc;n tiến lại vương ch&uacute;t cổ điển. Ch&iacute;nh l&agrave; nhắc tới nước hoa nam Gucci Guilty Black. Ra mắt c&oacute; mọi người v&agrave; năm 2013 l&agrave; 1 trong những chiếc nước hoa nam t&aacute;o bạo từng đạt được rộng r&atilde;i giải thưởng tr&ecirc;n thế giới.&nbsp;<strong>Gucci Guilty Black</strong>&nbsp;được mệnh danh l&agrave; m&ugrave;i hương hấp dẫn của thế kỷ 21. Sự phối hợp hai gam m&agrave;u xanh v&agrave; đen c&agrave;ng l&agrave;m cho sự t&aacute;o tợn, mạnh mẽ. N&oacute; được biểu hiện r&otilde; hơn, phần trọng t&acirc;m ch&iacute;nh l&agrave; in nổi logo của nh&atilde;n h&agrave;ng Gucci tạo điểm nhấn cho sản phẩm.</p>', 18, 2500000, 2000000, 1, '2023-03-04 02:35:14', '2023-03-04 02:35:14', '/storage/uploads/2023/03/04/1536303338.9734.png'),
(10, 'Bleu De Chanel EDP', 'loại nước hoa nam này thích hợp cho các chàng trai năng động vì hương thơm mạnh mẽ sẽ làm những cô nàng đổ gục lúc sắp bạn. Mùi hương thơm mạnh mẽ như vậy đâu thể 1-2 mang tiếng bay đi được, độ lưu hương sẽ giữ cho bạn từ 7-12 tiếng đồng hồ để bạn luôn tự tín trong mọi buổi giao lưu', '<p>loại&nbsp;<strong>nước hoa nam</strong>&nbsp;n&agrave;y th&iacute;ch hợp cho c&aacute;c ch&agrave;ng trai năng động v&igrave; hương thơm mạnh mẽ sẽ l&agrave;m những c&ocirc; n&agrave;ng đổ gục l&uacute;c sắp bạn. M&ugrave;i hương thơm mạnh mẽ như vậy đ&acirc;u thể 1-2 mang tiếng bay đi được, độ lưu hương sẽ giữ cho bạn từ 7-12 tiếng đồng hồ để bạn lu&ocirc;n tự t&iacute;n trong mọi buổi giao lưu</p>', 18, 3000000, 2500000, 1, '2023-03-04 02:37:31', '2023-03-04 02:37:31', '/storage/uploads/2023/03/04/OIP (1).jpg'),
(11, 'Tom Ford Noir For Men', 'Nồng độ là EDP nên là dòng nước hoa nam này sẽ lưu hương cho bạn khoảng 8-10 tiếng đồng hồ. Và để kéo dài hương thơm cho bạn suốt cả ngày.', '<p><strong>Nước hoa nam Tom Ford Noir For Men</strong>&nbsp;tới từ nh&atilde;n hiệu Tom Ford được ra mắt v&agrave;o năm 2012. Với n&eacute;t cổ điển giữa những thị th&agrave;nh hiện đại b&acirc;y giờ. D&ograve;ng nước hoa được ngo&agrave;i mặt hơi thuần tu&yacute; phần thủy tinh gam m&agrave;u tối ở tr&ecirc;n với t&ecirc;n sản phẩm m&agrave;u trắng. B&ecirc;n cạnh đ&oacute;, c&ograve;n về phần nắp sản phẩm được ngoại h&igrave;nh dạng kẻ nhỏ khiến vượt bậc sản phẩm hơn. Về m&ugrave;i hương cũng sẽ trải qua 3 tầng lớp c&oacute; cho bạn sự quyến rũ.</p>', 18, 4500000, 2950000, 1, '2023-03-04 02:40:55', '2023-03-04 02:40:55', '/storage/uploads/2023/03/04/Tom-Ford-Noir-for-Men-3-e1671720371600-300x300.jpg'),
(12, 'Calvin Klein CK – ONE', 'Độ lưu hương cũng ko quá dài từ 3-6 tiếng. Nhưng cũng ko vì thế mà mất đi mùi hương, CK One vẫn còn chút gì đó vương vít lại trên người bạn.', '<p><em><strong>Calvin Klein&nbsp;</strong></em><em><strong>CK &ndash; ONE</strong></em>&nbsp;l&agrave; d&ograve;ng nước hoa d&agrave;nh cho cả nam v&agrave; nữ tới từ nh&atilde;n hiệu Calvin Klein<strong>.&nbsp;</strong>Được ra mắt v&agrave;o năm 1994, CK One đ&atilde; l&agrave;m cho &lsquo;chao đảo&rsquo;&rsquo; bạn teen ưa chuộng nước hoa. V&agrave; đ&atilde; trở th&agrave;nh 1 tượng đ&agrave;i trong thập ni&ecirc;n 90. CK One tinh tế, quyến rũ kho&aacute;c tr&ecirc;n m&igrave;nh d&ograve;ng &aacute;o trắng thủy tinh mờ, phần nắp được l&agrave;m cho trong khoảng kim kh&iacute; bạc s&aacute;ng. V&agrave; nằm ở vị tr&iacute; ch&iacute;nh giữa l&agrave; t&ecirc;n sản phẩm. Ba tầng hương sẽ khiến cho bạn cảm nhận được từng ph&uacute;t chốc c&oacute; ch&uacute;t mạnh mẽ, ph&oacute;ng kho&aacute;ng sở hữu ch&uacute;t g&igrave; đấy nhẹ nh&otilde;m.</p>', 18, 1120000, 790000, 1, '2023-03-04 02:43:04', '2023-03-04 02:43:04', '/storage/uploads/2023/03/04/nuoc-hoa-calvin-klein-ck-ck-one-cho-ca-nam-va-nu-100ml-62070ada1c43b-12022022081818.jpg'),
(13, 'Valentino Uomo For Men', 'Nước hoa Valentino Uomo For Men sẽ giữ mùi hương trong khoảng thời kì 7-12 tiếng, bền bỉ cho những cuộc vui chơi cả ngày dài hay các bữa tiệc xuyên đêm.', '<p><strong><em>Valentino Uomo For Men</em></strong>&nbsp;được nh&agrave; s&aacute;ng chế Olivier Polge tạo ra v&agrave; tới năm 2014 nh&atilde;n hiệu Valentino đ&atilde; cho ra mắt mẫu nước hoa nam n&agrave;y. Nước hoa nam được lấy &yacute; tưởng từ những g&igrave; cổ điển trong đ&ocirc; thị th&agrave;nh phố n&agrave;y, vững chắc sẽ sở hữu những qu&yacute; &ocirc;ng ham sự giản dị v&agrave; m&ugrave;i hương của k&iacute; v&atilde;ng.</p>\r\n\r\n<p>bề ngo&agrave;i đầu chai h&igrave;nh v&ograve;m, th&acirc;n chai h&igrave;nh trụ thẳng đứng. Chất liệu khiến cho từ thủy tinh với c&aacute;c đường v&acirc;n kim cương nổi bật ph&iacute;a ngo&agrave;i biểu thị sự nam t&iacute;nh v&agrave; mạnh mẽ.</p>', 18, 3000000, 2886615, 1, '2023-03-04 02:45:02', '2023-03-04 02:45:02', '/storage/uploads/2023/03/04/61ZcxnDFnAL._SR476,476_.jpg'),
(14, 'Versace Pour Homme', 'Versace Pour Homme tập kết cho mình những nguyên tố của một người đàn ông lịch duyệt, tinh tế và giàu tình cảm. Sản phẩm với đa dạng dung tính cho bạn chọn lựa như 5ml, 30ml, 50ml, 100ml và 200ml.', '<p><em>Nước hoa nam Versace Pour Homme</em>&nbsp;được cho ra mắt năm 2008 đến từ nh&atilde;n hiệu Versace. M&ugrave;i hương nước hoa l&agrave; sự h&ograve;a quyện của chanh v&agrave;ng v&agrave; cam Bergamot. N&oacute; như một cơn gi&oacute; m&aacute;t thổi m&aacute;t giữa d&ograve;ng nắng của m&ugrave;a h&egrave; n&oacute;ng bức.</p>\r\n\r\n<p>Th&ecirc;m phần thiết kế nước hoa cũng rất thuần tu&yacute;, được l&agrave;m cho trong khoảng thủy tinh trong suốt bạn. Với thể nh&igrave;n thấy phần dung dịch m&agrave;u xanh nhạt b&ecirc;n trong. Bạn sẽ cảm nhận được sự tươi m&aacute;t v&agrave; nhẹ nh&otilde;m nhưng vẫn to&aacute;t l&ecirc;n vẻ nam t&iacute;nh bởi được mẫu m&atilde; phần g&oacute;c cạnh.</p>', 18, 3500000, 3199000, 1, '2023-03-04 02:46:42', '2023-03-04 02:46:42', '/storage/uploads/2023/03/04/OIP (2).jpg'),
(15, 'Hugo Boss Boss Bottled Oud Aromatic', 'Nước hoa nam Hugo Boss Boss Bottled Oud Aromatic là dòng nước hoa dành cho người phương Đông đến từ thương hiệu Hugo Boss. Chín là 1 thương hiệu chuyên về các mẫu nước hoa cao cấp. Sản phẩm được ra mắt vừa mới đây vào năm 2019.', '<p><em>Hugo Boss Boss Bottled Oud Aromatic</em>&nbsp;c&oacute; hương thơm chiết xuất kh&ocirc;ng phổ th&ocirc;ng m&ugrave;i mix sở hữu nhau, th&iacute;ch hợp cho c&aacute;c người đ&agrave;n &ocirc;ng lịch duyệt v&agrave; r&eacute;t mướt. Độ lưu hương sẽ từ 6-8 tiếng đủ để bạn tham gia mọi bữa tiệc hay đi khiến cả ng&agrave;y d&agrave;i. Với mẫu nước hoa n&agrave;y độ tỏa hương sẽ trong 1 c&aacute;nh tay của bạn, đủ để người đối diện ấn tượng c&oacute; bạn.&nbsp;<strong>Hugo Boss Boss Bottled Oud Aromatic</strong>&nbsp;sẽ l&agrave; tuyển lựa xuất sắc d&agrave;nh cho m&ugrave;a thu v&agrave; đ&ocirc;ng.</p>', 17, 2500000, 1990000, 1, '2023-03-04 02:48:04', '2023-03-04 02:48:04', '/storage/uploads/2023/03/04/OIP (3).jpg'),
(16, 'Good Girl Glorious Gold Carolina Herrera', 'Good Girl Glorious Gold Carolina Herrera được tiếp nối thành công của Good Girl từ khi ra mắt 2016 trứ danh của hãng.', '<p><strong><a href=\"https://www.thegioisonmoi.com/products/nuoc-hoa-good-girl-glorious-gold-carolina-herrera-edp-phien-ban-gioi-han-2019\">Good Girl Glorious Gold Carolina Herrera</a></strong>&nbsp;được tiếp nối th&agrave;nh c&ocirc;ng của Good Girl từ khi ra mắt 2016 trứ danh của h&atilde;ng.&nbsp;Phi&ecirc;n bản Limited Edition&nbsp;<strong><a href=\"https://www.thegioisonmoi.com/products/nuoc-hoa-good-girl-glorious-gold-carolina-herrera-edp-phien-ban-gioi-han-2019\">Good Girl Glorious Gold</a></strong>&nbsp;si&ecirc;u phẩm guốc nhũ v&agrave;ng c&ugrave;ng m&ugrave;i thơm xuất sắc. Note hương cam bergamot, hạnh nh&acirc;n quyện hoa hồng v&agrave; hoa di&ecirc;n vĩ. Ch&uacute;t vani v&agrave; cacao khiến m&ugrave;i hương bỗng trở l&ecirc;n ngọt ng&agrave;o, nồng n&agrave;n đầy quyến rũ.</p>', 19, 3650000, NULL, 1, '2023-03-04 02:54:37', '2023-03-04 02:54:37', '/storage/uploads/2023/03/04/tải xuống.jpg'),
(17, 'Chanel No.5 Eau De Parfum', 'Đây là loại nước hoa nữ trước nhất của hãng Chanel nổi danh. Chanel No.5 là một sản phẩm huyền thoại hiện nay. Và đã đi cùng mang nhiều cô gái ở mọi độ tuổi suốt cả một thập kỷ qua.', '<p>Hương trước hết m&agrave; ch&uacute;ng ta thuận tiện cảm nhận l&agrave; tinh dầu hoa hồng, Ylang-Ylang, hoa nh&agrave;i v&agrave; m&ugrave;i vani nhẹ nh&agrave;ng, tinh tế. Chanel No.5 đem đến hương giữa mang tinh dầu hoa hồng đậm đặc. B&ecirc;n cạnh đ&oacute;, hương cuối của đ&agrave;n hương, xạ hương c&ugrave;ng vani. Ch&iacute;nh sự pha trộn kh&eacute;o l&eacute;o từ nhiều d&ograve;ng tinh dầu kh&aacute;c nhau n&agrave;y. Đ&atilde; khiến cho Chanel No.5 mang 1 hương thơm đặc biệt. V&agrave; lưu giữ ấn tượng kh&oacute; phai trong t&acirc;m tưởng của rộng r&atilde;i người.</p>', 19, 4000000, 3400000, 1, '2023-03-04 02:56:53', '2023-03-04 02:56:53', '/storage/uploads/2023/03/04/nuoc-hoa-nu-3.jpg'),
(18, 'D&G Velvet Cypress', 'Ra mắt từ năm 2017 nhưng tới bây giờ D&G Velvet Cypress vẫn luôn là sự lựa chọn của phần đông bạn gái. Chai nước hoa nữ có thiết kế lọ thủy tinh sang trọng, đầy cuốn hút.', '<p>Ra mắt từ năm 2017 nhưng tới b&acirc;y giờ D&amp;G Velvet Cypress vẫn lu&ocirc;n l&agrave; sự lựa chọn của phần đ&ocirc;ng bạn g&aacute;i. Chai nước hoa nữ c&oacute; thiết kế lọ thủy tinh sang trọng, đầy cuốn h&uacute;t.&nbsp;</p>', 19, 4000000, 3450000, 1, '2023-03-04 02:58:34', '2023-03-04 02:58:34', '/storage/uploads/2023/03/04/OIP (4).jpg'),
(19, 'Dior J’adore', 'khi đề cập đến nhãn hàng Dior đắt giá thì ko người nào là ko biết tới loại nước hoa nữ. J’adore sở hữu mùi hương quyến rũ, ngọt ngào và vô cùng sang trọng.', '<p>J&rsquo;adore đem lại sự h&ograve;a quyện giữa m&ugrave;i hương hoả hồng Damascena Thổ Nhĩ Kỳ. V&agrave; c&ugrave;ng với tinh chất đ&agrave;m hương Sri Lanka v&agrave; hoa nh&agrave;i Grasse. Đ&ocirc;ng đảo được phối hợp lại tạo n&ecirc;n hương thơm đặc th&ugrave; trong J&rsquo;adore. N&oacute; kh&ocirc;ng qu&aacute; nồng m&agrave; biểu đạt sự tươi mới, nhẹ nh&agrave;ng v&agrave; kh&ocirc;n c&ugrave;ng nữ t&iacute;nh. Sản phẩm được ngoại h&igrave;nh c&oacute; vẻ ngo&agrave;i sang chảnh. Cộng với đường cong hấp dẫn vừa sở hữu phong c&aacute;ch ti&ecirc;n tiến pha ch&uacute;t cổ điển b&ecirc;n trong.</p>\r\n\r\n<p>Điều đặc biệt cho mẫu nước hoa n&agrave;y đ&oacute; l&agrave; c&aacute;c bản d&ugrave;ng thử 5ml. Đối với hững n&agrave;ng c&oacute; thể bỏ t&uacute;i dễ d&agrave;ng khi vận động bất cứ đ&acirc;u c&oacute; lọ nước hoa nữ mini Dior J&rsquo;adore n&agrave;y.</p>', 19, 3700000, 3450000, 1, '2023-03-04 03:00:29', '2023-03-04 03:00:29', '/storage/uploads/2023/03/04/nuoc-hoa-nu-dior-j-adore-edp-100ml-63b3e7af56598-03012023153039.png'),
(20, 'Gucci Flora', 'Gucci Flora là mẫu nước hoa nữ say mê của những cô nàng tuổi mới lớn. Nhãn hàng cao cấp nức tiếng thế giới Gucci luôn biết lấy lòng khách hàng.', '<p>Hương nước hoa được tổng h&ograve;a trong khoảng rộng r&atilde;i c&aacute;i tinh dầu. Chẳng hạn như hương cam qu&yacute;t, hoa c&aacute;i đơn rất tươi mới. Gucci Flora mang lại hương giữa l&agrave; m&ugrave;i thơm của hoả hồng v&agrave; hoa mộc tệ. V&agrave; hương kết th&uacute;c hương cưới với gỗ đ&agrave;n hương, hoắc hương v&agrave; tuyết t&ugrave;ng. Mẫu nước hoa nữ n&agrave;y giữ được m&ugrave;i hương rất l&acirc;u. Với độ lan tỏa mạnh v&agrave; kh&ocirc;n c&ugrave;ng ấm n&ecirc;n ph&ugrave; hợp sử dụng trong những ng&agrave;y m&ugrave;a đ&ocirc;ng gi&aacute; lạnh.</p>', 19, 2150000, 1750000, 1, '2023-03-04 03:01:54', '2023-03-04 03:01:54', '/storage/uploads/2023/03/04/nuoc-hoa-nu-gucci-flora-gorgeous-gardenia-edt-100ml-620a12117a931-14022022152553.jpg'),
(21, 'Dior Miss Dior Blooming Bouquet', 'Đây là dòng nước hoa nữ khôn xiết nổi danh. Và có nhẽ không thể vắng mặt trên các kệ mỹ phẩm của biết bao cô gái. Miss Dior Blooming Bouquet mang đến mùi hương khôn cùng thanh mát, nhẹ nhàng.', '<p>Trong khoảng hương đầu tới hương giữa được phối hợp. Bởi tinh dầu hoa c&aacute;i đơn, hu&ecirc; hồng c&ugrave;ng ch&uacute;t m&ugrave;i vị tươi m&aacute;t của cam Bergamot. Miss Dior Blooming Bouquet cho m&ugrave;i hương cuối l&agrave; xạ hương trắng đem đến sự mềm mại, nhẹ nh&agrave;ng. Từ đ&oacute;, gi&uacute;p những c&ocirc; n&agrave;ng vui vẻ, thảnh thơi. Hơn. Miss Dior ko qu&aacute; ngọt hay nồng m&agrave; chỉ dịu nhẹ, thanh khiết. N&ecirc;n rất th&iacute;ch hợp mang những bạn mới lần đầu sử dụng nước hoa. Ch&iacute;nh cho n&ecirc;n, ko c&oacute; g&igrave; l&agrave; kh&oacute; hiểu l&uacute;c Miss Dior lu&ocirc;n nằm trong top sản phẩm nước hoa nữ b&aacute;n chạy v&agrave; được đa dạng c&ocirc; g&aacute;i ham.</p>', 19, 3050000, 2800000, 1, '2023-03-04 03:04:23', '2023-03-04 03:04:23', '/storage/uploads/2023/03/04/nuoc-hoa-dior-miss-dior-blooming-bouquet-100ml-5cf1f44ef1854-01062019104310.jpg'),
(22, 'Carolina Herrera Good Girl Légère Eau De Parfum', 'Gợi cảm, quyến rũ, thu hút', '<p><strong>Good Girl Legere</strong>&nbsp;l&agrave; sự h&ograve;a quyện c&aacute;c hương hoa cỏ tươi s&aacute;ng, hương thực phẩm ngọt ng&agrave;o để rồi quyến rũ, l&ocirc;i cuốn hồn người đến kh&ocirc;ng thể cưỡng lại. Nốt hương đầu mở ra với ngọc lan t&acirc;y. Sau đ&oacute; nhường chỗ cho vẻ đẹp tinh khiết của nh&agrave;i trắng c&ograve;n lan sang hương giữa, kết hợp với hoa huệ đầy m&ecirc; đắm, c&oacute; thể l&agrave;m ng&atilde; gục những tr&aacute;i tim cuồng m&ugrave;i hương độc đ&aacute;o từ chiếc gi&agrave;y cao g&oacute;t&nbsp;<strong>Good Girl</strong>&nbsp;sang chảnh n&agrave;y. Hương cuối dai dẳng với đậu tonka v&agrave; gỗ đ&agrave;n hương tạo n&ecirc;n vẻ đẹp của&nbsp;<strong>Good Girl Legere</strong>.</p>', 17, 670000, 470000, 1, '2023-03-04 03:09:51', '2023-03-04 03:09:51', '/storage/uploads/2023/03/04/nuoc-hoa-carolina-herrera-good-girl-legere-eau-de-parfum-10ml-dang-lan-63297b8c27daf-20092022153628.jpg'),
(23, 'Prada Candy Eau De Parfum Spray Pen For Women', 'Ngọt ngào, cuốn hút, nữ tính', '<p><strong>Nước hoa Prada Candy Eau De Parfum Spray Pen For Women </strong>&nbsp;l&agrave; chai nước hoa mới được ra mắt trong năm 2021 của thương hiệu&nbsp;<strong>Prada</strong>. Hương thơm thơm ngọt ng&agrave;o của nh&oacute;m hương hoa cỏ phương đ&ocirc;ng cho n&agrave;ng th&ecirc;m cuốn h&uacute;t, nữ t&iacute;nh.</p>', 20, 790000, 470000, 1, '2023-03-04 03:13:13', '2023-03-04 03:13:13', '/storage/uploads/2023/03/04/nuoc-hoa-nu-prada-candy-eau-de-parfum-spray-pen-fo-women-0-33-oz-10-ml-dang-xit-63b689eac2c2d-05012023152722.jpg'),
(24, 'Chanel Bleu EDT', 'Mạnh mẽ, nam tính, sang trọng', '<p>Mạnh mẽ, nam t&iacute;nh, sang trọng</p>', 21, 700000, 500000, 1, '2023-03-04 03:16:01', '2023-03-04 03:16:01', '/storage/uploads/2023/03/04/nuoc-hoa-danh-cho-nam-chanel-bleu-edp-50ml-5cebb52f0a8ba-27052019170015.jpg'),
(25, 'Viktor & Rolf Flowerbomb Nectar Mini', 'Nữ tính, ngọt ngào, mạnh mẽ', '<p>Nữ t&iacute;nh, ngọt ng&agrave;o, mạnh mẽ</p>', 17, 680000, 470000, 1, '2023-03-04 03:17:12', '2023-03-04 03:17:12', '/storage/uploads/2023/03/04/nuoc-hoa-nu-viktor-rolf-flowerbomb-nectar-mini-10ml-6329696a3a4cd-20092022141906.jpg');

